#!/usr/bin/env python
# -*- coding: utf-8 -*-
from __future__ import unicode_literals  # unicode by default

from django.contrib.gis.db import models


class Update(models.Model):
    """An update generated by users interaction"""

    title = models.CharField(max_length=256, null=False)
    date = models.DateTimeField(auto_now=True)

    TYPES = (
        ("A", "add"),
        ("E", "edit"),
        ("D", "discussion"),
    )
    typ = models.CharField(max_length=1, null=False, choices=TYPES, db_index=True)
    object_type = models.CharField(max_length=32, null=False, db_index=True)
    object_slug = models.CharField(max_length=512, null=False)
    comments_count = models.IntegerField(null=True)

    # comma-separated list of usernames
    _users = models.CharField(max_length=1024)

    @property
    def users(self):
        return self._users.split(',')

    @users.setter
    def users(self, l):
        self._users = ','.join(l)

    # comma-separated list of communities slugs
    _communities = models.CharField(max_length=1024)

    @property
    def communities(self):
        return self._communities.split(',')

    @users.setter
    def communities(self, l):
        self._communities = ','.join([c.name for c in l])

    def __unicode__(self):
        return unicode(self.title)

    @property
    def name(self):
        return self.title
